mapper.select:
select id, name, reward, status, childId, rejectReason, DATE_FORMAT(createDate, '%y.%m.%d') AS createDate, DATE_FORMAT(doneDate, '%y.%m.%d') AS doneDate from missionDB.Mission WHERE childId = #{id}
and status = #{status}
and status > 1
ORDER BY createDate DESC, id DESC;

mapper.update:
update missionDB.Mission
    set status = 1
where id = #{id};

update missionDB.Mission
    set status = 3, doneDate = CURDATE(), rejectReason = #{rejectReason}
where id = #{id};

update missionDB.Mission
    set status = 2, doneDate = CURDATE()
where id = #{id};
